import math
import numpy as np
import platonic_solid as ps

def tetr_face(radius, sigma):
    return ps.tetr_face_write(radius, sigma)

def cube_face(radius, sigma):
    return ps.cube_face_write(radius, sigma)

def octa_face(radius, sigma):
    return ps.octa_face_write(radius, sigma)

def dode_face(radius, sigma):
    return ps.dode_face_write(radius, sigma)

def icos_face(radius, sigma):
    return ps.icos_face_write(radius, sigma)

def tetr_vert(radius, sigma):
    return ps.tetr_vert_write(radius, sigma)

def cube_vert(radius, sigma):
    return ps.cube_vert_write(radius, sigma)

def octa_vert(radius, sigma):
    return ps.octa_vert_write(radius, sigma)

def dode_vert(radius, sigma):
    return ps.dode_vert_write(radius, sigma)

def icos_vert(radius, sigma):
    return ps.icos_vert_write(radius, sigma)
